void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    uv -= 0.5;
    float count = 16.;

    mat2 transformation = mat2(
        cos(iTime), -sin(iTime),
        sin(iTime), cos(iTime)
    );
    vec2 mvUv = transformation * uv;

    vec2 cell = fract(mvUv * count);

    fragColor = vec4(cell.x * cell.y);
    fragColor.a *= 0.25;
}

