#define pi 3.1415926

vec3 colorDef(vec2 uv) {
    vec3 color;
    color.r = texture(iChannel0, uv).r;
    color.g = texture(iChannel0, uv).g;
    color.b = texture(iChannel0, uv).b;
    return color;
}


vec2 fault(vec2 uv, float s) {
    float v = pow(0.5 - 0.5 * cos(2.0 * pi * uv.y), 2000.0) * sin(2.0 * pi * uv.y);
    uv.x += v * s * 5.0;
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = iTime / 10.0;

	vec2 uv = fragCoord.xy / iResolution.xy;

//    int newValue = int(iTime*100.0);

//    if ((newValue % 2) > 0) { мигание полосы


    uv = fault(uv + vec2(0.0, fract(t * 2.0)), 0.2) - vec2(0.0, fract(t * 2.0));
//    } 
    vec3 color = colorDef(uv);
	fragColor = vec4(color, 1.0);
}
