void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
   float r = (iMouse.x - fragCoord.x)*(iMouse.x - fragCoord.x)+ (iMouse.y - fragCoord.y) * (iMouse.y - fragCoord.y) - float(sin(iTime * float(5) )*float(5000) + float(5000) ) ;

   float g = (iMouse.x - fragCoord.x)*(iMouse.x - fragCoord.x)+ (iMouse.y - fragCoord.y) * (iMouse.y - fragCoord.y) - float(sin(iTime * float(5.1) )*float(5000) + float(5000) ) ;
   float b = (iMouse.x - fragCoord.x)*(iMouse.x - fragCoord.x)+ (iMouse.y - fragCoord.y) * (iMouse.y - fragCoord.y) - float(sin(iTime * float(5.2) )*float(5000) + float(5000) ) ;


    vec3 col = vec3(-r,-g,-b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
