void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 z = ((2.0 * fragCoord - iResolution.xy) / iResolution.y) * 1.5;
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + vec2(-0.75, 0.0);
        if(dot(z, z) > 65536.0) break;
    }
    l = l - log2(log2(dot(z,z))) + 4.0;
    if(dot(z, z) < 65536.0) l = 100.0;
    fragColor = vec4(vec3(sqrt(l / 100.0)) * vec3(0.1, 1.0, 0.8), 1.0);
}
