void mainImage( out vec4 fragColor, in vec2 fragCoord ){
//note: option + return-> compile
    vec2 p = fragCoord/iResolution.xy;//pixel coord/rez->(0,1)
    float X = 0.25 - 0.1*cos(2.0*p.y)*cos(iTime);
    float Y = 0.7*cos(iTime);
    float width = 0.13;
    float sharpness = 0.01;
    float w = 0.01+0.02*abs(cos(10.0*exp(2.0*p.y)));
    w *= sqrt(exp(1.0-4.0*p.y)*0.3);
    w += exp(-70.0*p.y);
    vec2 q = p - vec2(X,Y);//dist off center
    float r = width + 0.07 * cos( atan(q.y,q.x)*10.0+20.0*q.x*cos(iTime)+1.0);//frequency, num of spikes
    r *= cos(iTime);
    float h = cos(p.x*10.0 + iTime)*0.1 + cos(p.x*5.0-1.0)*0.1; 
    float valley = 1.0-smoothstep(h,h+0.5,p.y)+0.3;
    valley *= 1.0-abs(cos(40.0*valley))*p.y;//get funky bro
    vec3 col = vec3(0.15,0.2,0.7);//sky

    col = mix(col,vec3(0.7,0.6,0.3),valley);//valley
    col = mix(vec3(0.3,0.5,0.3)*sqrt(p.y),col,max(smoothstep(w, w+sharpness, abs(q.x)),smoothstep(Y,Y+sharpness,p.y)));//stem
    col = mix(col,vec3(0.1,0.3,0.2) * exp(p.y),1.0-smoothstep(r, r + sharpness,length(q)));//canopies
    col.r *= floor(50.0*exp(p.y))*cos(iTime)/40.0;//red filter

    fragColor = vec4(col,1.0);
}
