    bool Circle(vec2 point, vec2 center, float r){
           float scaleY = iResolution.y / iResolution.x; //нормализация под размер экрана чтоб был кргу а не элипс
            return (point.x - center.x) * (point.x - center.x) + (point.y   - center.y) * (point.y - center.y) * (scaleY * scaleY)  < r*r; 
}
float calculate(vec2 a, vec2 b, vec2 point){
      return (point.x - a.x) * (b.y - a.y) - (point.y-a.y) * (b.x - a.x);
    }
 bool isOnOneSide(vec2 a, vec2 b, vec2 c, vec2 point){
      return calculate(a, b, c) * calculate(a, b, point) >= 0.;
    }
bool triangle(vec2 a, vec2 b, vec2 c, vec2 point){
      return  isOnOneSide(a, b, c, point) && isOnOneSide(b, c, a, point) && isOnOneSide(c, a, b, point);
    }



