// Create your own shader using GLSL Shader Language!
void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    float dy = 5.0 / iResolution.y;
    // xy is LR Frequency domain data
    vec2 fv = texture(iChannel0, vec2(uv.x, 0.5)).xy;
    // zw is LR Time domain data
    vec2 tv = texture(iChannel0, vec2(uv.x, 0.5)).zw;
    vec3 col = vec3(0.);

    // Time domain data
    if (!(uv.y + dy < tv.x) && (uv.y - dy < tv.x)) {
        // Color the pixel that crosses the time domain sample value
        col += 100.;
    }

    // Frequency domain data
    if (uv.y < fv.x) {
        col.gb += fv.x + 0.4;
    }

    fragColor = vec4(col,5);
}
