//  thanks to asherwolfstein
float random (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}


vec2 noise (vec2 p)
{
    return (1. + sin((p.xy * vec2(4.12,3.5312)) + p.yx * vec2(41.21, 15.2))) * 0.5;
}


vec2 noiseScaled (vec2 p, float scale)
{
    p *= scale;
    return (1. + sin((p.xy * vec2(4.12,3.5312)) + p.yx * vec2(41.21, 15.2))) * 0.5;
}


vec3 noise3d (vec2 p)
{
    vec2 rnd = noise(p);
    return vec3(rnd, fract(dot(rnd, vec2(12.231, 2.543))));
}

vec3 noiseColor(vec2 p)
{
//    vec2 rnd = noiseScaled(p, 1.0);
    vec2 rnd = noise(p * 0.3);
    return vec3(rnd, 1./*fract(dot(rnd, vec2(12.231, 2.543)))*/);
}


float text(vec2 fragCoord)
{
    vec2 uv = mod(fragCoord.xy, 16.)*.0625;
    vec2 block = fragCoord*.0625 - uv;
    uv = uv*.8+.1; // scale the letters up a bit
    uv += floor(noise(block + iTime * .002).xy * 16.); // randomize letters
    uv *= .0625; // bring back into 0-1 range
//    uv.x = -uv.x; // flip letters horizontally
    return texture(iChannel0, uv).r;
}

vec3 rain(vec2 fragCoord)
{
    vec2 uv = mod(fragCoord.xy, 16.)*.0625;
    vec2 block = (fragCoord*.0625 - uv);

    return noiseColor(block + iTime * .12);
