void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xx;

    float R = 0.1;
    float r = length(uv - vec2(0.5, 0.25));
    float v = 1.;
    if(r > R)
        v = R / r;

    float t = 0.25/sqrt(1. - (v*v));

    t = 0.5 + 0.5 * sin(t * 50.);
    vec3 col = vec3(t);

    if(r <= R)
        col = vec3(0.05,0.,0.15);

    // Output to screen
    fragColor = vec4(col,1.0);
}
