const int RAY_STEPS = 256;
const float FOVY = 3.1415 * 0.25;
const float TO_RADIANS = 3.1415 / 180.0;
const vec3 LIGHT_POS = vec3(0., 6., -3.);

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

float plane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}

#define BACK_WALL plane(pos, vec4(0.0, 0.0, -1.0, 5.0))
#define BACK_WALL_ID 1

#define LEFT_WALL plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define LEFT_WALL_ID 2

#define RIGHT_WALL plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_ID 3

#define CEILING_WALL plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define CEILING_WALL_ID 4

#define FLOOR plane(pos, vec4(0.0, 1.0, 0.0, 2.5))
#define FLOOR_ID 5

#define LEFT_BOX box(rotateY(pos + vec3(1.7, 1.3, -2), 25. * TO_RADIANS), vec3(1, 1, 1))
#define LEFT_BOX_ID 6

#define RIGHT_BOX box(rotateY(pos + vec3(-1.3, 1, -0.75), -25. * TO_RADIANS), vec3(1, 2, 1))
#define RIGHT_BOX_ID 7

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
} 

void fetchObject(vec3 pos, out float t, out int obj)
{
    t = BACK_WALL;
    obj = BACK_WALL_ID;

    float t2 = LEFT_WALL;
    if(t2 < t)
    {
        t = t2;
        obj = LEFT_WALL_ID;
    }
    t2 = RIGHT_WALL;
    if(t2 < t)
    {
        t = t2;
        obj = RIGHT_WALL_ID;
    }
    t2 = CEILING_WALL;
    if(t2 < t)
    {
        t = t2;
        obj = CEILING_WALL_ID;
    }
    t2 = FLOOR;
    if(t2 < t)
    {
        t = t2;
        obj = FLOOR_ID;
    }
    t2 = LEFT_BOX;
    if(t2 < t)
    {
        t = t2;
        obj = LEFT_BOX_ID;
    }
    t2 = RIGHT_BOX;
    if(t2 < t)
    {
        t = t2;
        obj = RIGHT_BOX_ID;
    }
}

float fetchObject(vec3 pos)
{
    float t = BACK_WALL;
    return min(min(min(min(min(min(t, LEFT_WALL), RIGHT_WALL), 
                CEILING_WALL), FLOOR), LEFT_BOX), RIGHT_BOX);
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( fetchObject(pos + epsilon.yxx) - fetchObject(pos - epsilon.yxx),
                            fetchObject(pos + epsilon.xyx) - fetchObject(pos - epsilon.xyx),
                            fetchObject(pos + epsilon.xxy) - fetchObject(pos - epsilon.xxy)));
}
vec3 lambertian(int hitObj, vec3 p, vec3 n, vec3 lightDir)
{
    float cosine = dot(-lightDir, n);
    switch(hitObj){
        case LEFT_BOX_ID:
        return vec3(1., 1., 1.) * cosine;
        break;
        case RIGHT_BOX_ID:
        return vec3(1., 1., 1.) * cosine;
        break;
        case BACK_WALL_ID:
        return vec3(1., 1., 1.) * cosine;
        break;
        case LEFT_WALL_ID:
        return vec3(0., 1., 0.) * cosine;
        break;
        case RIGHT_WALL_ID:
        return vec3(1., 0., 0.) * cosine;
        break;
        case CEILING_WALL_ID:
        return vec3(1., 1., 1.) * cosine;
        break;
        case FLOOR_ID:
        return vec3(1., 1., 1.) * cosine;
        break;
        case -1:
        return vec3(0., 0., 0.);
    }
}

void march(vec3 origin, vec3 dir, out float t, out int objectID)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        fetchObject(pos, m, objectID);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.;
    objectID = -1;
}


Intersection sdf(vec3 dir, vec3 eye)
{
    float t;
    int objectID;
    march(eye, dir, t, objectID);
    vec3 intersection = eye + t * dir;
    vec3 normal = computeNormal(intersection);
    vec3 lightDir = normalize(intersection - LIGHT_POS);
    vec3 color = lambertian(objectID, intersection, normal, lightDir);
    return Intersection(t, color, intersection, objectID);
}


vec3 shootRay(vec3 eye, vec3 ref, vec2 ndc)
{
    vec3 eye2Ref  = ref - eye;
    float n = length(eye2Ref);

    vec3 worldUp = vec3(0.0, 1.0, 0.0);
    vec3 right  = normalize(cross(eye2Ref, worldUp));

    vec3 U = normalize(cross(right, eye2Ref));
    vec3 V = U * n * tan(FOVY * 0.5);

    float aspect = iResolution.x / iResolution.y;
    vec3 H = right * n * tan(FOVY * 0.5) * aspect;

    vec3 p = ref + ndc.x * H + ndc.y * V;
    return normalize(p - eye);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) * 2. - vec2(1.0, 1.0);
    vec3 eye = vec3(0, 2, -13);
    vec3 ref = vec3(0, 2, 0);
    vec3 lookAt = shootRay(eye, ref, uv);
    Intersection it = sdf(lookAt, eye);
    fragColor = vec4(it.color, 1.0);
}
