float PI = 3.14,
     PHI = 1.61,
     RE = .5,
      RI = .4,
       n = 10.;

float drawCircles(vec2 U, float RE, float RI, float a) {

    float r = length(U), c = 0.;

    for(float r,i = 0.; i++ < n; a += 2.*PI/n)
        r = length(U - RE * vec2( -sin(a), cos(a)) ),
        c += exp(-abs(r - RI)/.008);

    return c;
}

void mainImage( out vec4 O, vec2 u ) {
    vec2 R = iResolution.xy, 
        U = ( 2. * u - R ) / R.y;   
    O=vec4(0.);
    for(float i=0.,p=1.; i++ < 30.; p*=PHI)
        O += drawCircles(U, RE * p, RI * p, i * iTime/4.) * vec4(1,1,0,0);
}
