void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.3 + 0.7*cos(iTime+uv.xyx+vec3(0,2,4));

    // Read in the texture
    vec3 txt = texture(iChannel0, uv).rgb;

    // Colour is mouse
    vec2 mouseuv = (iMouse.xy-fragCoord.xy)/iResolution.xy;
    float mouse = min(1.0, length(mouseuv) < 0.15 ? 1.0 : .03/(length(mouseuv)-0.15) ); 

    fragColor = vec4(mouse*txt + (1.0-mouse)*col*txt,1.0);
}
