void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/iResolution.xy;

    vec4 sLResNoise = texture(iChannel2, xy);
    vec4 lResNoise = texture(iChannel1, xy);
    vec4 hResNoise = texture(iChannel0, xy);

    lResNoise.g = lResNoise.r; lResNoise.b = lResNoise.g;
    hResNoise.g = hResNoise.r; hResNoise.b = hResNoise.g;
    sLResNoise.g = sLResNoise.r; sLResNoise.b = sLResNoise.g;

    vec4 color = vec4(0.0,0.0,0.0,1.0);

    color.r = (sLResNoise.r + lResNoise.r + hResNoise.r) / 3.0; 
    color.g = (sLResNoise.r + lResNoise.r + hResNoise.r) / 3.0;
    color.b = (sLResNoise.r + lResNoise.r + hResNoise.r) / 3.0;

    fragColor = color;
}
