float circle(vec2 uv, float r, float diff)
{    
    float d = length(uv);
    float circ = smoothstep(r, r - diff, d);
    return circ;
}

float Band(float uv, float start, float end, float blur)
{
    float step1 = smoothstep(start - blur, start + blur, uv);
    float step2 = smoothstep(end + blur, end - blur, uv);    
    return step1 * step2;       
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur)
{
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    return band1 * band2;
}

float Smiley(vec2 uv, vec2 p, float size)
{
    uv -= p;
    uv /= size;

    float mask = circle(uv, .3, 0.02);    

    uv -= .1;
    float rEye = circle(uv, .05, .01);
    mask -= rEye;

    uv.x += .2;
    float lEye = circle(uv, .05, .01);
    mask -= lEye;

    uv += vec2(-.1, .1);
    float mouth = circle(uv, .2, .001);    
    uv.y -= .05;
    mouth -= circle(uv, .2, .001);

    mask -= mouth;
    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord / iResolution.xy;
    float t = iTime;

    uv -= .5;
    //uv.x *= iResolution.x / iResolution.y;  

    float mask = 0.;

    //Smiley(uv, vec2(0., 0.), 1.);       

    float y = uv.y;    
    float x = uv.x + sin(8.*y + t) * .1;

    float blur = .001+ .2*(y+.4)/.8;
    blur = pow(blur, .4);

    // trapezium
    // mask = Rect(vec2(x, y), -.2 + y*.2, .2 - y*.2, -.2, .2, .001);
    mask = Rect(vec2(x, y), -.05, .05, -.4, .4, blur);

    vec3 color = vec3(1., 1., 1.) * mask;

    fragColor = vec4(color, 1.0);
}
