#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
void mainImage(out vec4 v4, in vec2 v2){
    v2-=iResolution.xy/2.0;v2*=2.0;
    vec2 o1=mod(v2*rot(radians(0.0)),64.0)-32.0;
    vec2 o2=mod(v2*rot(radians(30.)),64.0)-32.0;
    vec2 o3=mod(v2*rot(radians(60.)),64.0)-32.0;;
    v4=vec4(length(o1)*length(o2)*length(o3)-5000.0-10000.*(sin(iTime)+1.0));
}
