// defines/constants
#define SDF_DIST .01
#define RAYMARCH_STEPS 250
#define MAX_DIST 50.0

struct Ray {
    vec3 dir;
    vec3 origin;
};

float opUnion( float d1, float d2 )
{
    return min(d1,d2);
}

float opSmoothUnion( float d1, float d2, float k )
{
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
	//float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
	//return mix( d2, d1, h ) - k*h*(1.0-h);
}

float GetMinSceneDistanceFromPoint(vec3 point) {
    //define sphere here for now vec4(position.xyz, radius)
    vec4 sphere = vec4(0.0, 1.0, 6.0, 1.0);
    float plane_y = -0.0;

    // get distance from point to sphere
    // return opUnion(length(point - sphere.xyz) - sphere.w, point.y + plane_y); // no blending
    return opSmoothUnion(length(point - sphere.xyz) - sphere.w, point.y + plane_y, 0.5); // blending
}

float Raymarch(Ray r)
{
    float dist_0 = 0.0; //distance from origin

    // main raymarch loop
    for(int i=0; i < RAYMARCH_STEPS; i++) {

        // march ray from origin in direction
        vec3 t = r.origin + r.dir * dist_0;

        // get distance
        float d = GetMinSceneDistanceFromPoint(t);

        //advance/march along ray
        dist_0 += d;

        //compute sdf
        if(dist_0 > MAX_DIST || d < SDF_DIST) {
            // found a hit
            return dist_0;
        }
    } 
}

float calcShading(vec3 p) 
{
    // light source
    vec3 light_position = vec3(-5.0, 5.0, 2.0);

    // light direction
    vec3 light_dir = normalize(light_position - p);

    // calculate hitpoint normal (gradient of sdf at p)
    float dist = GetMinSceneDistanceFromPoint(p);
    vec2 epsilon = vec2(0.01, 0);
    vec3 normal = normalize(dist - vec3(GetMinSceneDistanceFromPoint(p - epsilon.xyy), 
                                        GetMinSceneDistanceFromPoint(p - epsilon.yxy), 
                                        GetMinSceneDistanceFromPoint(p - epsilon.yyx)));

    // calculate diffuse contribution
    float diffuse_contrib = clamp(dot(normal, light_dir), 0.0, 1.0);

    // calculate shadow rays
    Ray shadowray;
    shadowray.origin = p + normal * SDF_DIST * 2.0;
    shadowray.dir = light_dir;
    float shadow_fac = Raymarch(shadowray);

    // in shadow
    if(shadow_fac < length(light_position - p)) diffuse_contrib *= 0.5;

    return diffuse_contrib;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Create ray
    Ray ray;
    ray.dir = normalize(vec3(uv.x, uv.y, 1.0));
    ray.origin = vec3(0.0, 1.0, 0.0);

    // raymarching
    float m = Raymarch(ray);

    if(m < MAX_DIST) {
        // calculate hit point
        vec3 hitpoint = ray.origin + ray.dir * m;

        // shade point
        float diffuse = calcShading(hitpoint);
        fragColor = vec4(vec3(diffuse), 1.0);
    }
    else
    {
        // background
        vec3 col = vec3(uv.xy, 1.0);
        fragColor = vec4(col,1.0);
    }

}
