void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.14159265359;

    float aspR = iResolution.x/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv.y /= aspR;


    vec2 mouseUV = iMouse.xy/iResolution.xy;
    mouseUV.y /= aspR;

    float fStar = sin(iTime*5.)*sin(2.*round(9.*(sin(iTime/5.)+1.)/2.+1.)*atan((uv.y-mouseUV.y)/(uv.x-mouseUV.x)));

    float distR = 0.5 + 0.5*sin(1. -iTime*3.0 + fStar+length(uv - mouseUV)*(51.0 + 10.1*sin(iTime*5.0)));
    float distG = 0.5 + 0.5*sin(2. -iTime*3.0 + fStar+length(uv - mouseUV)*(50.0 + 10.5*sin(iTime*5.0)));
    float distB = 0.5 + 0.5*sin(1.5 -iTime*3.0 + fStar+length(uv - mouseUV)*(52.0 + 10.9*sin(iTime*5.0)));

    vec3 c = vec3(smoothstep(0.2, 0.8, distR), smoothstep(0.3, 0.7, distG), smoothstep(0.1, 0.9, distB));




    fragColor = vec4(c, 1.0);
}
