void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = SI(fragCoord);
}

void mainImage( out vec4 fragColor, in vec2 fgi )
{
    fgi -= iResolution.xy*0.5;
    fgi = abs(fgi);

    fragColor = SI(fgi);
}

float Rand(float t)
{
    return fract(600.*(sin(mod(t, 50000.))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float id = fragCoord.x * fragCoord.y + iTime + iDate.w;

    vec3 col = vec3(
        Rand(id + iDate.w*iDate.z),
        Rand(id + iDate.w+iDate.z),
        Rand(id + iDate.w-iDate.z)
        );

    fragColor = distance(iMouse.xy, fragCoord) < 5. || Rand(id)-0.99 > 0.00099? vec4(col, 1.) : SI(fragCoord)*Cooling;
}
