void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color1 = vec3(1.0, 1.0, 0.0);
    vec3 color2 = vec3(0.0, 0.0, 0.0);
    float alpha = 1.0;
    float borderX = iMouse.x;
    float borderY = iMouse.y;
    if ((fragCoord.x < borderX || fragCoord.x > borderX + 1.0) && (fragCoord.y < borderY || fragCoord.y > borderY + 1.0))
    {
        fragColor = vec4(color2, alpha);
    }
    else
    {
        fragColor = vec4(color1, alpha);
    }



}
