#define f (fragColor.rgb)
#define uv(x) (fragCoord+x)/iResolution.xy
#define size 8
#define strength 4.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    f=texture(iChannel0,uv(0.)).rgb;
    vec2 brightest=vec2(0);
    float len=length(texture(iChannel0,uv(0.)).rgb);
    int toosame=0;
    for(int i=-size;i<size;i++)
    for(int j=-size;j<size;j++){
        float nlen=length(texture(iChannel0,uv(vec2(i,j))).rgb);
        if(abs(nlen-len)<0.01)toosame++;
        if (nlen>len){
            brightest=vec2(i,j);
            len=nlen;
        }

    }
    if(toosame<size)f+=strength/dot(brightest,brightest);





}
