void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float r = -0.005*(fragCoord.x)/iResolution.x-0.997;
    float z = 2.0*fragCoord.y/iResolution.y-1.0;

	//-----------------------------------------------
    // Plot the attractor    
	//-----------------------------------------------
    float x = -0.1;
    float y = -0.2;
    float f = 0.0;

    // get rid of the transient state
    for( int i=0; i<2000; i++ ) {x = r*x-y; y =x*y;}
    // start plotting
    for( int i=0; i<2048; i++ )
    {
        x = r*x-y; y =x*y;
        f += 0.1*exp(-400000.0*(z-x)*(z-x));
    }

    vec3 col = vec3(max(1.0-f,0.0));

		fragColor = vec4( col, 1.0 );
}
