mat3 laplacian = mat3(
-1, -1, -1,
-1,  8, -1, 
-1, -1, -1
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float value = 0.0;
    float p;

    for(int i=-1; i<=1; i++) {
    for(int j=-1; j<=1; j++) {
        p = texelFetch(iChannel0, ivec2(fragCoord)+ivec2(i, j), 0).x;
        p *= laplacian[i+1][j+1];

        value += p;
    }}

    fragColor = vec4(value/4., value/4., value/4., 1.0);
}

vec2 point[1] = vec2[](vec2(0.5, 0.5));

float pDist(vec2 p1, vec2 p2, float p) {
    vec2 r = abs(p1-p2);
    r = pow(r, vec2(p, p));

    float dist = dot(r, vec2(1.0, 1.0));
    dist = pow(dist, 1.0/p);

    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float p = 2.0; //p values

    float dist1 = pDist(fragCoord, point[0]*iResolution.xy, p);
    float dist2 = pDist(fragCoord, iMouse.xy, p);

    float t = mod(iTime*20.0, 100.0); //time shifting effect

    // Normalized pixel coordinates (from 0 to 1)
    float value = sqrt(dist1*dist2);
    value = float(mod(value-t, 100.0)>50.0);


    // Output to screen
    fragColor = vec4(value, value, value, 1.0);
}
