void mainImage( out vec4 O, vec2 U )
{
    float i = floor(3.*U.x/iResolution.x), 
        ofs = i < 1. ? 0. : i < 2. ? .625 : .3;

    U = floor(U);
    O = vec4 (mod(U+ofs,U ) == vec2(ofs) );  // test whether mod is exact

    if ( mod( 3.*U.x, iResolution.x ) < 6. ) O = vec4(0,1,0,1); // separator
}
