float random(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

vec4 get(vec2 uv){
    vec4 fragColor = vec4(0,0,0,0);
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float r = 0.07+0.005*(pow(abs(cos(iTime)),280.0)+pow(abs(cos(iTime+0.2)),280.0))+0.01*cos(iTime);
    float eq = 0.0;
    for(float i = 0.0; i< 30.0;i++){
        float x = (1.0-2.0*random(vec2(i*5.0+2.0,3.0)))/4.0/4.0+cos(iTime*random(vec2(i*2.2,3.5))+3.0*random(vec2(i*3.1,3.9)))/8.0;
        float y = (1.0-2.0*random(vec2(i*3.0+7.0,6.0)))/4.0/4.0+cos(iTime*random(vec2(i*3.0,5.8))+3.0*random(vec2(i*3.9,-0.15)))/8.0;
        float d = (sqrt((uv.x + x)*(uv.x+x)+(uv.y+y)*(uv.y+y)));
        eq += r*r/d;
        col += vec3(d,d,d)/20.0;
    }
    if(eq > 1.0)
        fragColor = vec4(col,1.0);
    else
        fragColor = vec4(col*sqrt(uv.x*uv.x+uv.y*uv.y)/5.0,1.0);
    return fragColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-iResolution.xy/2.0)/iResolution.x;
    for(float i = 0.0; i < 0.001; i += 0.001)
    fragColor = (get(uv)+get(uv*(1.0+i))+get(uv*(1.0-i)))/3.0;
}
