// pseudo-shaded variant of https://shadertoy.com/view/sdlfRj
// + animated zoom

// Xor golfed down 12 chars. See more readable version below

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U *= 7./R;
    O-=O;
    for(float i=-60.,a,z = 3.+sin(iTime/2.); i++ < 9.;
        O += ( mod(i,2.)* ( .7 - .3*sin(a) ) - O ) 
            * smoothstep(0.,15./R.y, 4./z*cos(a) -i/6. - U.y ))
        a = z*.5* ( U.x - sin( i/6. + iTime ) );
}





