precision highp float;

const float PI = 3.14;
const float e = 2.71;

const float R0 = e/PI;
const float eyesR = 0.2;
const float eyesInnerR = (4./5.) * eyesR;
const vec2 eyePoint = vec2(.3, .2);

const vec4 background = vec4(0., 0., 0., 1.);

const vec2 pointA = vec2(-R0, 0.);
const vec2 pointB = vec2(R0, 0.);

vec4 drawEyes(vec2 uv, float R, float angle) {
    vec3 eyeA = vec3(1.);
    vec3 eyeB = vec3(1.);
    float halo = 0.08;

    for(int i = 0; i < 5; i++) {
        float angle = float(i + 1) * PI/5.;
        vec2 pointC = vec2(eyesR * cos(angle), eyesR * sin(angle)) + eyePoint;
        float r = distance(uv, pointC);

        float dec = pow(e, (-r + eyesInnerR)/halo);
        eyeA *= vec3(dec);
    }

    for(int i = 0; i < 5; i++) {
        float angle = float(i + 1) * PI/5.;
        vec2 pointC = vec2(eyesR * cos(angle), eyesR * sin(angle)) + eyePoint;
        float r = distance(vec2(-uv.x, uv.y), pointC);

        float dec = pow(e, (-r + eyesInnerR)/halo);
        eyeB *= vec3(dec);
    }

    return vec4(eyeA + eyeB, 1.);
}

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

vec4 draw(vec2 uv, float R, float angle) {
    float r = length(uv);

    vec2 pointC = vec2(R * cos(angle), R * sin(angle));
    vec2 outerPointC = vec2(R * R * cos(angle), R * R * sin(angle));

    vec3 circleColor = vec3(pow(e, -abs(R - r) * 10.));

    vec2 cv = pointC - uv;
    vec2 vc = uv - pointC;
    float scalarProduct = dot(cv, vc);
    float dec = pow(e, -abs(scalarProduct) * 100.);

    vec3 snakeHeadColor = background.rgb;
    snakeHeadColor = vec3(pow(e, -(distance(uv, pointC) + 0.5)/1.));

    vec2 uv1 = rotZ(vec2(uv.x + pointA.x, uv.y), angle/2.);
    vec3 line1Color = vec3(pow(e, -abs(uv1.x) * 200.));
    vec2 uv2 = rotZ(vec2(uv.x + pointB.x, uv.y), (angle - PI)/2.);
    vec3 line2Color = vec3(pow(e, -abs(uv2.x) * 200.));
    vec3 line3Color = vec3(pow(e, -abs(uv.y) * 200.));

    return vec4(circleColor, 1.) +
        vec4(snakeHeadColor, 1.) + vec4(line1Color, 1.) + vec4(line2Color, 1.)
        + vec4(line3Color, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    float angle = iTime * e/PI + PI/2.;

    vec4 mainColor = draw(uv, R0, angle);
    fragColor = (mainColor + drawEyes(uv, R0, angle)) * sin(angle);
}
