void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 x = texture(iChannel0, fragCoord.xy / iResolution.xy).zxy;

    vec3 c = vec3(1,0,1) * x.xzy;

    c = 1.- max(c, x);

    fragColor = vec4(c, 1);
}

/*
	See: A Simple Model of the Belousov-Zhabotinsky Reaction From First Principles
	by Alasdair Turner http://discovery.ucl.ac.uk/17241/1/17241.pdf
*/

#define TIMESTEP .1

bool reset() {
    return texture(iChannel3, vec2(32.5/256.0, 0.5) ).x > 0.5;
}

#define T(d) n += texture(iChannel0, fract(vUv+d)).xyz + texture(iChannel0, fract(vUv+d*2.0)).xyz + texture(iChannel0, fract(vUv+d*3.0)).xyz;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vUv = fragCoord.xy / iResolution.xy;
    vec4 t = vec4(1. / iResolution.xy, -1. / iResolution.y, 0.0);
    vec3 p = texture(iChannel0, vUv).xyz;
    vec3 n = vec3(0);

    // shorthand for summing the values over all 8 neighbors
    T(t.wy) T(t.xy) T(t.xw) T(t.xz)   
    T(t.wz) T(-t.xy) T(-t.xw) T(-t.xz)


    n/=8.0;

    // this line encodes the rules
    vec3 r = vec3(n.z - n.y, n.x - n.z, n.y - n.x);
    vec3 result = p - TIMESTEP *r;
    result = clamp(result, 0.0, 1.0);

    // initialize with noise
    if(p.xyz == vec3(0) || reset()) {
        fragColor = texture(iChannel1, vUv);
    } else {
        fragColor = vec4(result, 0.0);
    }

}
