#define PI (atan(1.)*4.)
#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
#define M(p,n)vec2(asin(sin(atan(p.x,p.y)*n))/n,1)*length(p)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<90.;
        O.xyz+=mix(vec3(1),H(log(s)*.3),.8)*.02*exp(-.2*i*i*e)
    )
    {
        p=g*d;
        q=p=R(p,normalize(vec3(1,1,2)),.5*sin(iTime*.1));
        p+=vec3(.3,.5,iTime*.3);
        p=sin(2.7*p+5.*sin(p*.3));
        s=3.;
        for(int i;i++<6;)
            p=abs(p-1.7)-1.6,
            s*=e=2.3/clamp(dot(p,p),.1,1.2),
            p=abs(p)*e;
        g+=e=length(p.zy)/s;
    }
}
