vec2 points[10] = vec2[10](vec2(4.,2.), vec2(1.,1.), vec2(0.5,1.), vec2(0.25,0.75),vec2(8.,4.),vec2(8.,4.),vec2(8.,4.),vec2(8.,4.),vec2(8.,4.),vec2(8.,4.));
vec3 colours[10] = vec3[10](vec3(1., 0., .0),vec3(0.0, 1.0, 0.0), vec3(0.0, 0.0, 1.0), vec3(.2, .1, 0.8),vec3(1., 0., .0),vec3(1., 0., .0),vec3(1., 0., .0),vec3(1., 0., .0),vec3(1., 0., .0),vec3(1., 0., .0));
float rand(float co) { 
    return fract(sin(co*(91.3458)) * 47453.5453); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
   // uv.x-=0.5;
    uv*=5.;
    vec2 xy = vec2(iMouse.x/iResolution.y, iMouse.y/iResolution.y)*5.;

   for(int i=0; i<10;i++){
       points[i] = vec2(rand(floor(iTime)+float(i))*5.,rand(floor(iTime+2.)+float(i))*5.);
   }
   for(int i=0; i<10;i++){
       colours[i] = vec3(rand(floor(iTime)+float(i)),rand(floor(iTime+9.)+float(i)),rand(floor(iTime+5.)+float(i)));
   }

    vec3 col = vec3(0.);
    float td = 0.;

    int meatballs = 4;
    points[1] = xy;
    colours[1] *= 1.2;
    for(int i=0; i<meatballs;i++){
        float dist = length(uv - points[i]);
        float inv = (1. /dist);
        col += 1.1*colours[i] * inv*inv  ;
        td += inv*inv;


    }
   //for(int i=0; i<meatballs;i++){
   //   col *= smoothstep(0.0,0.03, length(uv - points[i]));
   //}

    col/=td;
    // Output to screen
    fragColor = vec4(col,1.0);
}
