precision highp float;

const float PI = 3.14;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;
    vec2 uv2 = fragCoord.xy/iResolution.xy;
    float anim = 20. * sin(iTime/2.);
    float y = sin(anim * uv.x + (iTime + iMouse.x/10.)*PI/2.) * sin(anim * uv.y + (iTime + iMouse.y/10.)*PI/2.);
    vec3 color = vec3(abs(y));
    vec3 tColor = texture(iChannel0, mod(uv2, 1.)).rgb;

    fragColor = vec4(tColor * color, 1.);
}
