precision highp float;

const float PI = 3.14;
const float e = 2.71;

const float R = 0.5;
const mat3 colors = mat3(0., 0., 1., 0., 1., 0., 1., 0., 0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;
    vec3 circles = vec3(0.);
    float halo = abs(sin(iTime/2.)) * 0.3 + 0.01;

    for(int i = 0; i < 3; i++) {
        float angle = float(i + 1) * PI/1.5 + iTime/2. + PI/2.;
        vec2 pointC = vec2(R * cos(angle), R * sin(angle));
        float r = distance(uv, pointC);

        float dec = round(pow(e, (-r + 0.1)/halo) * 10.)/10.;
        circles += colors[i % 3].rgb * vec3(dec);
    }

    fragColor = vec4(circles, 1.);
}
