void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // time period
    float duration = 0.6;
    float maxAlpha = 0.5;
    float maxScale = 1.6;

    // modular calculation on shader playback time to time period 
    // Comment out following line and try out the reversing effect as well
    // float progress = duration / mod(iTime, duration); 
    float progress = mod(iTime, duration) / duration; 
    float alpha = maxAlpha * (1.0 - progress);
    float scale = 1.0 + (maxScale - 1.0) * progress;

    float stretchedX = 0.5 + (uv.x - 0.5) / scale;
    float stretchedY = 0.5 + (uv.y - 0.5) / scale;

    vec2 stretchedTextureCoords = vec2(stretchedX, stretchedY);
    vec4 stretchedScene = texture(iChannel0, stretchedTextureCoords);
    vec4 originalScene = texture(iChannel0, uv);

    // Output to screen
    // fragColor = originalScene * alpha + stretchedScene * alpha;
    // fragColor = originalScene * alpha + stretchedScene * (1.0 - alpha);
    fragColor = originalScene * (1.0 - alpha) + stretchedScene * alpha;
}
