#define TAU 6.283185
#define WAVES 50.0
#define CONTORTION 2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.x = uv.x + 0.02 * sin(WAVES * uv.y + iTime);
    uv.y = uv.y + 0.02 * sin(WAVES * uv.x + iTime);

    float a = atan(uv.y - 0.5, uv.x - 0.5);
    a -= atan(uv.y - 0.25, uv.x - 0.25);
    a -= atan(uv.y - 0.75, uv.x - 0.75);
    a -= atan(uv.y - 0.25, uv.x - 0.75);
    a -= atan(uv.y - 0.75, uv.x - 0.25);

    vec3 v = fract(texture(iChannel0, uv).rgb + vec3(CONTORTION * a / TAU) + vec3(iTime*1.2648, iTime * 1.0, iTime * .87618));
    if (v.x > 0.5) v.x = 0.5 - (v.x - 0.5);
    if (v.y > 0.5) v.y = 0.5 - (v.y - 0.5);
    if (v.z > 0.5) v.z = 0.5 - (v.z - 0.5);
    v *= 2.0;

    fragColor = vec4(v, 1.0);
}
