#define BOUNCE 1
#define TUNNEL 1
#define ROCKS 1
#define FOG 1
#define LIGHTS 1

// noise approach credit: bigwings
float hash11(float p) {
    p = fract(p*234.34);
    p = fract(p+(p*(p+23.)));
    return p;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // bounce
    float jf = 0.01825;
    float tf = 0.001;
    vec2 j = jf*vec2(hash11(iTime*tf), hash11((iTime+200.)*tf));
#if BOUNCE
    uv += j;
#endif

    float x = uv.x;
    float y = uv.y;
    float d = length(uv);

    // tunnel
#if TUNNEL
    x = atan(uv.x, uv.y)/3.14*3.;
    x += .15*sin(iTime*2.2); // sway
    y = .5/d;
#endif

    y += iTime; // movement

    // color
    vec3 col = vec3(1);
    vec2 tc = vec2(x, y);
#if ROCKS
    col = texture(iChannel0, tc).rgb; // rocks
#endif

#if FOG
    col *= d*d; // fog
#endif

#if LIGHTS
    col = pow(col+.77, vec3(11.25)); // lights
#endif
    col *= smoothstep(1.8, 15.5, iTime); // fade

    fragColor = vec4(col,1.0);
}
