#define N 25.
#define pi 3.14159265358979
const float intense = 5.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = vec2(uv.x*N,uv.y*N);

    vec4 t = texture(iChannel0, floor(q)/N);
    float v = (t.r+t.g+t.b)/3.;
    float revision = (1.-abs( fract(q.x)-.5 )/.5);

    if (  abs( fract(q.y)-( (cos(iTime*3.0 + q.x*2.*pi * floor(v*intense))*revision+1.0 )*.35 ) )<0.05 ){
        fragColor = vec4(max(0.1,v*revision));//vec4(t*(revision));
    }else{fragColor = vec4(0.0);}
}
