float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float taxi(vec2 uv)
{
return abs(uv.x) + abs(uv.y);
}

const float pi = 3.14159;
const float pi2 = 1. * pi / 90.;
const float pi3 = -1. * pi / 90.;

float func(vec2 dir, float t)
{
float theta = atan(dir.y,dir.x);

float b = 0.5 * (1. + cos(t));
float d = sqrt(pow(b * taxi(dir),2.))+
sqrt(pow((1.-b) *taxi(dir + 0.3 * cos(1. * theta) * vec2(cos(t),sin( -t))),2.));
d *= 1.65 + 0.1 * cos(0.2 * t);

float d2 = 1. - length(dir + 0.3 * sin(1. * theta) * vec2(cos(-t),sin(t)));
d = sqrt(-0.5 + d * d + d2 * d2);
return step(d ,0.3);
}

vec3 colfunc(vec2 dir, float t)
{
return vec3(func(dir,t + pi2), func(dir,t),func(dir,t + pi3));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float t =iTime + 4.3;
    vec2 uv = fragCoord/iResolution.y -vec2(0.375,0);
    //uv = vec2(floor(176. * uv.x) / 176., floor(99. * uv.y) / 99.);

vec2 dir = uv - 0.5;

  vec3 col = colfunc(dir,t );
  for (int i = 0; i <5; i++)
  {    
  vec2 ex  = (0.05 + 0.04 * cos(0.2 * t)) * vec2(cos(t + float(i) * 2. * pi / 5.),sin(t + float(i) * 2. * pi / 5.));
  vec3 next =  colfunc(dir + ex,t + 0.15 * float(i));
col = abs(col-next); 
  }
    // Output to screen
    fragColor = vec4(col,1.0);
}
