#define k 360.0

vec2 rotate(vec2 xy, float a){
  return vec2(xy.x*cos(a)-xy.y*sin(a),xy.x*sin(a)+xy.y*cos(a));
}

bool gear(vec2 xy, float angle){
    xy=rotate(xy, angle);
    float rad=atan(xy.y,xy.x);
    if((length(xy)+pow(abs(sin(rad*4.0)),0.5)*k/15.0<k/6.0&&length(xy)<k/8.0&&length(xy)>k/12.0
    ||(abs(xy.x)<k/120.0||abs(xy.y)<k/120.0||abs(xy.x+xy.y)<k/90.0||abs(xy.x-xy.y)<k/90.0)&&length(xy)<k/8.0||length(xy)<k/24.0)
    &&length(xy)>k/36.0)
        return true;
    return false;
}

void mainImage(out vec4 rgba, in vec2 xy)
{
    float t=iTime;
    rgba=vec4(0.0);
    xy-=iResolution.xy/2.0;
    xy+=vec2(mod(xy.x,8.0),mod(xy.y,8.0));
    vec4 color1=vec4(1,(sin(xy.x)+cos(xy.y)+2.0)/4.0,(sin(xy.x)+cos(xy.y)+2.0)/4.0,1);
    vec4 color2=vec4((sin(xy.x)+cos(xy.y)+2.0)/4.0,1,(sin(xy.x)+cos(xy.y)+2.0)/4.0,1);
    vec4 color3=vec4((sin(xy.x)+cos(xy.y)+2.0)/4.0,(sin(xy.x)+cos(xy.y)+2.0)/4.0,1,1);
    vec4 color4=vec4(1,1,(sin(xy.x)+cos(xy.y)+2.0)/4.0,1);
    vec4 color5=vec4((sin(xy.x)+cos(xy.y)+2.0)/4.0,1,1,1);
    vec4 color6=vec4(1,(sin(xy.x)+cos(xy.y)+2.0)/4.0,1,1);
    vec4 color7=vec4((sin(xy.x)+cos(xy.y)+2.0)/2.0);
    if(gear(xy+vec2(0,0),t))rgba=color7;
    if(gear(xy+vec2(k/5.0,k/7.5),-t))rgba=color4;
    if(gear(xy+vec2(k/5.0,-k/7.5),-t))rgba=color5;
    if(gear(xy+vec2(k/2.5,0),t))rgba=color2;
    if(gear(xy+vec2(-k/5.0,k/7.5),-t))rgba=color6;
    if(gear(xy+vec2(-k/5.0,-k/7.5),-t))rgba=color1;
    if(gear(xy+vec2(-k/2.5,0),t))rgba=color3;
}
