    vec2 mouse;
    mouse.x = (iMouse.x/iResolution.y)/4.0+0.01;
    mouse.y = (iMouse.y/iResolution.y)/4.0+0.01;
    float size=2.0 + 98.0*iMouse.y/iResolution.y;
    float spacing =9./size;
    for (float y=1.;y<=size;y++){
        for (float x=1.;x<=size*1.2;x++){
            vec2 c0 = vec2(0.15*spacing*x, 0.1*spacing*y) ;
            vec2 c1 =c0 + vec2(0.15*spacing*sin(4.0*iTime*y/size ),- 0.1*spacing*sin(4.0*iTime*x/size));//
