        }
    }
	float thickness=1.0 ;//(100.-size)/20.0;
	float antialiasThickness=2.0;
    float density=smoothstep(thickness/iResolution.y, 
                        (thickness+antialiasThickness)/iResolution.y, d);
    fragColor = vec4(vec3(density),
                        density);
}
