// Fork of "Collatz Conjecture." by RJVB09. https://shadertoy.com/view/NslBWs
// 2022-02-22 19:56:00

void mainImage( out vec4 RGBA, in vec2 XY )
{
    int n = int(texelFetch(iChannel0, ivec2(XY), 0).r);

    RGBA = vec4(vec3(float(n) / 300.0), 1.0);
}

void mainImage( out vec4 RGBA, in vec2 XY )
{
    ivec2 IJ = ivec2(XY);

    int n;

    const int maxHeight = 300;

    if (IJ.y % maxHeight == 0) {
        n = IJ.x + (IJ.y / maxHeight) * int(iResolution.x);
    }
    else {    
        n = int(texelFetch(iChannel0, ivec2(IJ.x, IJ.y - 1), 0).r);

        if (n > 1) {
            if (n % 2 == 1)
            {
                n = n * 3 + 1;
            }
            else
            {
                n /= 2;
            }
        }
    }

    if (iMouse.z > 1.0) {
        n = 0;
    }

    RGBA = vec4(float(n));
}
