//////////////////

// MATERIAL IDS;
const int MAT_METAL = 0;
const int MAT_WEAVE = 1;
const int MAT_TILE = 2;
const int MAT_STARS = 3;
const int MAT_WOOD = 4;
const int MAT_ROCK = 5;
const int MAT_STONE = 6;
const int MAT_MIRROR = 7;

SDFSample Scene(vec3 p)
{
    vec3 pPlatform = p;
    SDFSample sPlatform = Cylinder(p, 0.1, 2.0);
    sPlatform.f = 4.0;
    sPlatform.m = MAT_WOOD;

    mat4 rCylinder = MRotate(sin(-TIME) * PI * 0.25, UP) * MRotate(PI / 2.0, RIGHT);
    vec3 pCylinder = p - RIGHT * sin(TIME) * PI * 0.125 - UP * 0.3;
    pCylinder = mul(rCylinder, pCylinder);

    SDFSample sCylinder = Cylinder(pCylinder, 1.0, 0.25); 
    sCylinder.r = rCylinder;
    sCylinder.m = MAT_MIRROR;
    sCylinder.f = 4.0;

    mat4 rSphere = MRotate(TIME, UP);
    vec3 pSphere = mul(rSphere, p - UP * 0.3 - LEFT - FORWARD * 0.25);
    SDFSample sSphere = Sphere(pSphere, 0.25);
    sSphere.f *= 8.0;
    sSphere.r = rSphere;

    SDFSample sScene = Union(sPlatform, sCylinder);
    sScene = Union(sScene, sSphere);

    return sScene;
}

vec3 GetNormal(vec3 p)
{
	vec2 e = vec2(NORMAL_CHECK_OFFSET, 0);

	vec3 n = Scene(p).d - vec3(
		Scene(p - e.xyy).d,
		Scene(p - e.yxy).d,
		Scene(p - e.yyx).d
		);
	return normalize(n);
}

float GetAO(vec3 p, vec3 n)
{
    float stepSize = AMBIENT_OCCLUSION_STEP_SIZE;
    float t = stepSize;

    float ac = 0.0;

    for(float i = 0.0; i < AMBIENT_OCCLUSION_STEP_COUNT; i++) {
        float d = Scene(p + n * t).d;
        ac += t - d;
        t += stepSize;
    }
    return 1.0 - ac / 2.0;
}

float GetShadows(vec3 ro, vec3 rd, float tMax)
{
    float res = 1.0;
    float t = SHADOW_CORRECTION;
    for( int i=0; i<50; i++ )
    {
        float h = Scene(ro + rd * t).d;
        res = min( res, tMax*h/t );
        t += clamp(h, 0.005, 0.5);
        if(res < 0.005 || t > tMax) break;
    }
    return clamp(res, 0.0, 1.0);
}

SDFSample SampleScene(vec3 origin, vec3 rayDir, out vec3 p, out float distFromOrigin, out bool hit, out bool outline)
{
    float outlineWidth = OUTLINE_WIDTH / iResolution.x;
    p = vec3(0.0);
    float prevDist = MAX_DIST;
    SDFSample s;

    distFromOrigin = 0.0;
    hit = false;
    outline = false;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        p = origin + rayDir * distFromOrigin;
		s = Scene(p);
		distFromOrigin += s.d;

        if (s.d < outlineWidth * pow(distFromOrigin, OUTLINE_DIST_COMP))
		{
			outline = true;
		}

        if (distFromOrigin >= MAX_DIST)
		{
			break;
		}

		if (s.d < SURFACE_DISTANCE_TOLERANCE)
		{
            hit = true;
			outline = false;
			break;
		}

        if (outline && prevDist < s.d)
        {
            hit = false;
            break;
        }

        prevDist = s.d;
    }

    return s;
}

vec3 GetLight(vec3 p, LightSource ls, MaterialSample mat, float ao, vec3 normal)
{
    vec3 lightDir = normalize(p - ls.p);

    float lightDist = distance(p, ls.p);
    float shadowValue = GetShadows(p, -lightDir, lightDist);

    float attenuation = 1.0 / (pow(lightDist, 2.0));
    float light = ls.s * attenuation;

    float scalar = dot(-lightDir, normal);
    scalar = clamp(scalar, 0.0, 1.0);

    light *= scalar;
    light *= shadowValue;
    light += ao * ls.s * attenuation;   

    return light * ls.c;
}

vec2 GetRegionalCoord(vec2 uv, vec3 region)
{
    return (fract(uv) + region.xy) / region.z;
}

vec3 GetTriPlanarTexture(vec3 p, vec3 normal, sampler2D sampler, vec3 region)
{
    vec3 texXY = texture(sampler, GetRegionalCoord(p.xy * 0.5 + 0.5, region)).rgb;
    vec3 texXZ = texture(sampler, GetRegionalCoord(p.xz * 0.5 + 0.5, region)).rgb;
    vec3 texYZ = texture(sampler, GetRegionalCoord(p.zy * 0.5 + 0.5, region)).rgb; 

    normal = abs(normal);
    //normal = pow(normal, vec3(2.0));
    //normal /= SumComponents(normal);

    vec3 tex = texXY * normal.z + texXZ * normal.y + texYZ * normal.x;

    return tex;
}

MaterialSample SampleSurface(SDFSample s, vec3 rayDir, vec3 normal)
{
    MaterialSample ms;
    vec3 p = s.p * s.f;
    ms.m = 0.0;
    ms.s = 0.0;
    normal = mul(s.r, normal);

    vec3 localNormal = mul(s.r, normal);
    vec3 iridescent = vec3(NSin(localNormal.r), NSin(localNormal.g), NSin(localNormal.b));

    switch(s.m)
    {
        case MAT_METAL:
            ms.c = GetTriPlanarTexture(p, normal, iChannel1, vec3(0.0, 1.0, 4.0));
            ms.s = 0.8;
            ms.m = 0.4;
            ms.c = mix(ms.c, ms.c * iridescent, 0.75);
            break;
        case MAT_WEAVE:
            ms.c = GetTriPlanarTexture(p, normal, iChannel1, vec3(0.0, 0.0, 4.0));
            break;
        case MAT_TILE:
            ms.c = GetTriPlanarTexture(p, normal, iChannel1, vec3(1.0, 0.0, 4.0));
            break;
        case MAT_STARS:
            ms.c = GetTriPlanarTexture(p, normal, iChannel1, vec3(1.0, 1.0, 4.0));
            break;
        case MAT_WOOD:
            ms.c = GetTriPlanarTexture(p, normal, iChannel1, vec3(1.0, 0.0, 2.0));
            ms.s = 0.95;
            ms.m = 0.1;
            break;
        case MAT_ROCK:
            ms.c = GetTriPlanarTexture(p, normal, iChannel1, vec3(0.0, 1.0, 2.0));
            break;
        case MAT_STONE:
            ms.c = GetTriPlanarTexture(p, normal, iChannel1, vec3(1.0, 1.0, 2.0));
            break;
        case MAT_MIRROR:
            ms.c = GetTriPlanarTexture(p, normal, iChannel1, vec3(0.0, 1.0, 4.0));
            ms.m = 0.7;
            ms.s = 0.99;
            ms.c *= iridescent;
            break;
    }

    return ms;
}

vec3 ColorCorrection(vec3 color)
{
    if (COLOR_BIT_DEPTH < 8.0)
    {
        float colorsPerChannel = pow(2.0, COLOR_BIT_DEPTH);
        color = floor(color * colorsPerChannel) / colorsPerChannel;
    }

    color = (color - vec3(0.5)) * CONTRAST + vec3(0.5);
    color = pow(color / 255.0, vec3(1.0 / GAMMA)) * 255.0;
    color = mix(vec3(AvgComponentLength(color)), color, SATURATION);
    color = mix(color, vec3(1.0) - color, NEGATIVE);
    color *= TINT;

    return color;
}

vec3 Render(vec3 camPos, vec3 camDir)
{
    float distFromOrigin = 0.0;
    bool hit;
	bool outline;

    vec3 rayOrigin = camPos;
    vec3 rayDir = camDir;

    // Light Sources
    LightSource ls1 = LightSource(vec3(8.0, 8.0, -12.0), WHITE, 200.0);

    SDFSample s;
    vec3 p = vec3(0.0);
    vec3 finalColor;
    vec3 bgColor;
    float reflectiveStrength = 1.0;
    float mainDistRatio = -1.0;
    int hitCount = 0;
    MaterialSample ms;

    // Do raymarching
    do {
        distFromOrigin = 0.0;
        hit = false;
        outline = false;

        bgColor = GradientSky(rayDir, vec3(0.1, 0.0, 0.3), vec3(1.0, 0.5, 0.2), vec3(0.3, 0.7, 1.0), 0.1);
        s = SampleScene(rayOrigin, rayDir, p, distFromOrigin, hit, outline);

        vec3 color = BLACK;

        float distRatio = distFromOrigin / MAX_DIST;
        mainDistRatio = mainDistRatio < 0.0 ? distRatio : mainDistRatio;
        vec3 normal = GetNormal(p);

        float scalar = clamp(dot(-rayDir, normal), 0.0, 1.0);

        // Apply color to pixel
        if (hit)
        {
            ms = SampleSurface(s, rayDir, normal);
            color = ms.c * (1.0 - ms.m);
            float ao = GetAO(p, normal);

            vec3 light = GetLight(p, ls1, ms, ao, normal);

            color *= light;

            color = mix(BLACK, color, scalar);
        }
        else if (outline)
        {
            color = BLACK;
        }
        else
        {
            color = bgColor;
        }

        finalColor += color * reflectiveStrength;
        reflectiveStrength *= max(ms.m * reflectiveStrength, mix(0.0, 1.0, pow(1.0 - scalar, 4.0)));
#if DIFFUSE_REFLECTION      
#if REFLECTION_ANGLE_VARIANCE
        vec3 pSeed = p * iTime;
#else
        vec3 pSeed = p;
#endif
        rayDir = normalize(reflect(rayDir, mix(NNormal(normal, N33(pSeed)), normal, ms.s)));
#else
    rayDir = reflect(rayDir, normal);
#endif
        rayOrigin = p + normal * SURFACE_DISTANCE_TOLERANCE * 1.05;
        hitCount++;
    }
    while (hit && !outline && hitCount < MAX_RAY_HITS);

    // Saturate and apply distance fade
    finalColor = clamp(finalColor, vec3(0.0), vec3(1.0));
    finalColor = mix(finalColor, bgColor, pow(mainDistRatio, FADE_ATTENUATION));

    return finalColor;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Alter resolution for old-school look
    fragCoord *= RESOLUTION_RATIO;
    fragCoord = floor(fragCoord) / RESOLUTION_RATIO;
    vec2 uv = fragCoord / iResolution.xy;

    // Split-screen stereoscopy
#ifdef STEREO_ENABLED
    bool leftEye = uv.x >= 0.5;
    uv.x = fract(uv.x * 2.0);
    uv.x *= 0.5;
    uv -= vec2(0.25, 0.5);
#else
    uv -= vec2(0.5);
#endif

    // Vignette
    float uvLength = length(uv);
    uvLength = uvLength * 1.33333 - .25;
    float vignette = smoothstep(0.99, 0.001, uvLength);

    // Aspect ratio
    float aspectRatio = iResolution.x / iResolution.y;
    uv.x *= aspectRatio;

    // Camera Stuff
    vec3 camPos = textureLod(iChannel0, vec2(0.5, 0.5)/iResolution.xy, 0.0).xyz;

    // Camera look direction
    vec3 camRot = textureLod(iChannel0, vec2(1.5, 0.5)/iResolution.xy, 0.0).xyz;
    mat4 yawMat = MRotate(camRot.x, UP);
    mat4 pitchMat = MRotate(camRot.y, RIGHT);
    mat4 rollMat = MRotate(camRot.z, FORWARD);
    mat4 rotMat = yawMat * pitchMat * rollMat;
    vec3 camDir = mul(rotMat, normalize(vec3(uv.x, uv.y, 1.0)));

#ifdef STEREO_ENABLED
    // Stereo eye-positioning
    camPos += rotMat * ((leftEye ? LEFT : RIGHT) * INTERPUPILARY_DISTANCE * 0.5);
#endif

    // Render the scene
    vec3 color = Render(camPos, camDir);

    // Apply color correction
    color = ColorCorrection(color);

#ifdef SCAN_LINES_ENABLED
    if (mod(fragCoord.y * RESOLUTION_RATIO, 2.0) < 1.0) color = BLACK;
#endif

    // Fade scene in at start
    color *= smoothstep(FADE_IN_DELAY, FADE_IN_DELAY + FADE_IN_LENGTH, iTime);
    color *= vignette;

    // Output to screen
    fragColor = vec4(color, 1.0);
}

