// RAYMARCHING
#define MAX_STEPS 2000
#define MAX_DIST 100.0
#define SURFACE_DISTANCE_TOLERANCE 0.001
#define NORMAL_CHECK_OFFSET 0.0001
#define MAX_RAY_HITS 3

// TIMING
#define TIME iTime * 1.0

// OUTLINE
#define OUTLINE_WIDTH 0.0
#define OUTLINE_DIST_COMP 0.7 // 0.0 lines shrink linearly with distance, 0.5 - lines stay same world-width at all distances, 1.0 - lines stay same screen width at distance 

// LIGHTING
#define FADE_ATTENUATION 10.0
#define AMBIENT_OCCLUSION_STEP_SIZE 0.006
#define AMBIENT_OCCLUSION_STEP_COUNT 20.0
#define AMBIENT_OCCLUSION_ATTENUATION 1.0
#define SHADOW_CORRECTION 0.001
#define DIFFUSE_REFLECTION 1
#define REFLECTION_ANGLE_VARIANCE 1

// SCENE START
#define FADE_IN_DELAY 0.1
#define FADE_IN_LENGTH 1.0

// COMMON CONSTANTS
#define TAU 6.283185
#define PI  TAU / 2.0
#define ROOT2 1.414213
#define ROOT3 1.732051
#define EULER 2.718282
#define MIDENTITY mat4(1.0)

// AXIAL VECTORS
#define FORWARD vec3(0.0, 0.0, 1.0)
#define BACKWARD vec3(0.0, 0.0, -1.0)
#define LEFT vec3(-1.0, 0.0, 0.0)
#define RIGHT vec3(1.0, 0.0, 0.0)
#define UP vec3(0.0, 1.0, 0.0)
#define DOWN vec3(0.0, -1.0, 0.0)

// COLORS
#define BLACK vec3(0.0)
#define BLUE vec3(0.0, 0.0, 1.0)
#define BROWN vec3(0.5, 0.2, 0.0)
#define DARK_GREY vec3(0.25, 0.25, 0.25)
