// CAMERA PARAMETERS
#define INIT_CAM_POS vec3(-1.2, 0.5, -0.3)
#define INIT_CAM_YAW -1.0
#define INIT_CAM_PITCH -0.3
#define INIT_CAM_ROLL 0.0
#define ROTATION_SPEED 8.0
#define BASE_MOVE_SPEED 2.0
#define MOVE_ACCELERATION 1.01

// UPDATE AND RECORD INPUT
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(0.0,0.0,0.0,1.0);

    if (int(fragCoord.y) == 0 )
    {
        if (int(fragCoord.x) <= 1)
        {
            // CAMERA CONTROLS
            vec4 camPos = texture(iChannel0, vec2(0.5, 0.5) / iResolution.xy, -100.0);
            vec4 camRot = texture(iChannel0, vec2(1.5, 0.5) / iResolution.xy, -100.0);
            vec2 mousePosData = texture(iChannel0, vec2(3.5, 0.5) / iResolution.xy, -100.0).xy;

            float yawAngle = camRot.x - ROTATION_SPEED * mousePosData.x / iResolution.x;
            float pitchAngle = clamp(camRot.y + ROTATION_SPEED * mousePosData.y / iResolution.y, -PI / 2.0, PI / 2.0);
            float rollAngle = camRot.z;

            //if (ReadKey(iChannel1, KEY_Q, false))
            //{
            //    rollAngle -= ROTATION_SPEED * iTimeDelta;
            //}

            //if (ReadKey(iChannel1, KEY_E, false))
            //{
            //    rollAngle += ROTATION_SPEED * iTimeDelta;
            //}

            if (camRot.w == 0.0 || ReadKey(iChannel1, KEY_F, false))
            {
                yawAngle = INIT_CAM_YAW;
                pitchAngle = INIT_CAM_PITCH;
                rollAngle = INIT_CAM_ROLL;
            }

            if (int(fragCoord.x) == 0)
            {
                // CAMERA POSITION
                vec3 moveDir = vec3(0.0);

                mat4 yawMat = MRotate(yawAngle, UP);
                mat4 pitchMat = MRotate(pitchAngle, RIGHT);
                mat4 rollMat = MRotate(rollAngle, FORWARD);
                mat4 rotMat = yawMat * pitchMat * rollMat;

                float moveSpeed = camPos.w;

                if (camRot.w == 0.0 || ReadKey(iChannel1, KEY_F, false))
                {
                    // SET INIT MOVE SPEED
                    moveSpeed = BASE_MOVE_SPEED;
                }

                // ACCELERATE/DECELERATE
                if (ReadKey(iChannel1, KEY_UP, false))
                {
                    moveSpeed *= MOVE_ACCELERATION;
                }
                if (ReadKey(iChannel1, KEY_DOWN, false))
                {
                    moveSpeed /= MOVE_ACCELERATION;
                }

                // MOVEMENT
                if (ReadKey(iChannel1, KEY_W, false))
                {
                    moveDir += mul(rotMat, FORWARD);
                }
                if (ReadKey(iChannel1, KEY_S, false))
                {
                    moveDir += mul(rotMat, BACKWARD);
                }
                if (ReadKey(iChannel1, KEY_A, false))
                {
                    moveDir += mul(rotMat, LEFT);
                }
                if (ReadKey(iChannel1, KEY_D, false))
                {
                    moveDir += mul(rotMat, RIGHT);
                }
                if (ReadKey(iChannel1, KEY_SPACE, false))
                {
                    moveDir += mul(rotMat, UP);
                }
                if (ReadKey(iChannel1, KEY_SHIFT, false))
                {
                    moveDir += mul(rotMat, DOWN);
                }            

                vec3 movement = vec3(0.0);
                if (length(moveDir) > 0.0)
                {
                    movement = normalize(moveDir) * iTimeDelta * moveSpeed;
                }

                if (camRot.w == 0.0 || ReadKey(iChannel1, KEY_F, false))
                {
                    // ADD INIT POSITION
                    camPos.xyz = INIT_CAM_POS;
                    movement = vec3(0.0);
                }

                fragColor = vec4(camPos.xyz + movement, moveSpeed);
            }
            else
            {                
                // CAMERA ROTATION
                fragColor = vec4(yawAngle, pitchAngle, rollAngle, 1.0); // w value 1.0 means no longer first frame
            }
        }
        else if (int(fragCoord.x) == 2)
        {
            // MOUSE CLICK
            vec4 mouseClickData = texture(iChannel0, vec2(2.5, 0.5) / iResolution.xy, -100.0);            
            fragColor = vec4(iMouse.z, mouseClickData.x, IsNewClick(mouseClickData.xy) || IsPersistentClick(mouseClickData.xy) ? iMouse.xy : vec2(0.0));
        }
        else if (int(fragCoord.x) == 3)
        {
            // MOUSE MOVEMENT
            vec4 mouseClickData = texture(iChannel0, vec2(2.5, 0.5) / iResolution.xy, -100.0);
            vec4 mousePosData = texture(iChannel0, vec2(3.5, 0.5) / iResolution.xy, -100.0);
            vec2 mouseDelta = IsPersistentClick(mouseClickData.zw) ? iMouse.xy - mouseClickData.zw : vec2(0.0);
            vec2 mouseCumulative = mousePosData.zw + mouseDelta;
            fragColor = vec4(mouseDelta, mouseCumulative);
        }
    }
}

