{
    vec2 uv = fragCoord / iResolution.xy;
    uv *= 2.0;

    if (uv.x > 1.0)
    {
        if (uv.y >= 1.0)
        {
            fragColor = texture(iChannel3, uv);
        }
        else
        {
            fragColor = texture(iChannel1, uv);
        }
    }
    else
    {
        if (uv.y >= 1.0)
        {

            fragColor = texture(iChannel2, uv);
        }
        else
        {
            fragColor = texture(iChannel0, uv);
        } 
    }
}

