void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float scale = 10.0;
    float x = uv.x*iResolution.x/scale;
    float y = uv.y*iResolution.y/scale;
    float pi = 3.1415926535897932384626;
    float checker = round(sin(x+round(sin(y)/2.0+0.5)*pi)/2.0+0.5);
    vec3 col = vec3(checker,checker,checker);

    fragColor = vec4(col,1.0);
}
