void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 diffuse = texture(iChannel0, uv);
    vec3 normal = vec3(texture(iChannel1, uv));
    vec3 lightDirection = vec3(cos(iTime), sin(iTime), 1.0);
    fragColor = diffuse * dot(normal, lightDirection);
}
