//water caustic
//source:https://www.shadertoy.com/view/MdKXDm
float Func(inout vec4 k,mat3 m,float scale)
{
    mat3 mt=m*scale;
    k.xyw*=mt;
    return length(0.5-fract(k.xyw));
}

void mainImage(out vec4 k, vec2 p)
{
    mat3 m=mat3(-2,-1,2, 3,-2,1, 1,2,2);

    k=iDate;
    k.z=0.0;
    k.xy = p*iDate.w*2e-7;
    float m1=min(Func(k,m,0.5),Func(k,m,0.4));
    float m2=min(m1,Func(k,m,0.3));
    k = pow(m2,7.)*25. +vec4(0,.0,.0,1);
}
