float dot2( in vec2 v ) { return dot(v,v); }

 float heart( in vec2 p, in float r ) 
{
    vec2 s = vec2(1.0, 0.0);

    p = p-s.xy;
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(.25,.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (3.0*fragCoord-iResolution.xy)/iResolution.y;


	float d = heart(p,.5);

	// coloring
    vec3 col = vec3(.1) - sign(d)*sin(iTime+p.y+vec3(0,0,0));

    col *= 1.0 - exp(-9.0*abs(d));




	fragColor = vec4(col,1.0);
}
