precision highp float;

vec3 WHITE = vec3(1);
vec3 RED = vec3(206, 17, 39)/255.;

float PI = 3.1415;

vec2 RotateUV(vec2 uv, float angle) {
    return vec2(
        cos(angle) * uv.x + sin(angle) * uv.y,
        cos(angle) * uv.y - sin(angle) * uv.x
    );
}

vec3 Flag(vec2 uv) {
    vec2 st = RotateUV(uv, PI/4.);

    vec3 col = mix(RED, WHITE, step(0.0, -uv.y));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    float t = uv.x*7.-2.*iTime+uv.y*3.;
    uv.y += sin(t)*.05 * (uv.x + 0.5);    

    vec3 col = Flag(uv);
    col *= mix(1., .7+cos(t)*.3, (uv.x + 0.5));

    fragColor = vec4(col, 1.0);
}
