// https://godotshaders.com/snippet/square/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;



    vec2 uv2 = uv;



    vec3 color = vec3(.2,.4,.2);
    float grid = 0.75;


    uv = mod(uv, grid) - grid / 2.0;

    // shapes
    float dots = step(.3,distance(uv, vec2(0.0)));
    vec3 dotsColor = vec3(0.536, 0.189, .895);
    vec3 layer1 = mix(dotsColor, color, dots);

    // giant dot in top layer
    float bigDot = 1.0 - step(0., uv2.x-uv2.y);
    vec3 bigDotColor = vec3(0.835, 0.198, .371);
    vec3 layer2 = mix(bigDotColor, color, bigDot);



    // blend modes
    // -- multiply
    color = layer1 * layer2;
    // -- screen
    color = 1. - (1. - layer1) * (1. - layer2); 
    // -- difference
    color = abs(layer1 - layer2);
    // -- exclusion
    color = layer1 + layer2 - 2. * layer1 * layer2;
    // -- overlay
    vec3 base = layer2;
    vec3 blend = layer1;
    vec3 limit = step(0.5, base);
    color = mix(2.0 * base * blend, 1.0 - 2.0 * (1.0 - base) * (1.0 - blend), limit);
    // -- color dodge
    color = layer1 / (1.0 - layer2);
    // -- linear dodge
    color = layer1 + layer2;
    // -- burn
    color = 1. - (1. - layer1) / layer2;
    // -- linear burn
    color = layer1 + layer2 - 1.0;







    // color = min(layer1, layer2);
    // color = max(layer1, layer2);


    // color = vec3(p.x);

    color = clamp(color, 0., 1.0);
    fragColor = vec4(color,1.0);
}
